// Mock data for demo purposes (when API key is not configured)
function displayMockData() {
    const mockData = {
        weather: [{ 
            description: 'partly cloudy', 
            icon: '02d' 
        }],
        main: {
            temp: 18,
            feels_like: 20,
            humidity: 65,
            pressure: 1013
        },
        wind: {
            speed: 3.5
        },
        visibility: 10000,
        dt: Math.floor(Date.now() / 1000)
    };
    displayWeather(mockData);
}

async function fetchWeather() {
    try {
        // Check if we have a real API key
        if (API_KEY === 'demo_key') {
            displayMockData();
            return;
        }

        const response = await fetch(API_URL);

        if (!response.ok) {
            throw new Error(`API Error: ${response.status} - ${response.statusText}`);
        }

        const data = await response.json();
        displayWeather(data);

    } catch (error) {
        console.error('Error fetching weather:', error);

        if (error.name === 'TypeError' && error.message.includes('fetch')) {
            displayError('Network connection failed. Showing demo data instead.');
            setTimeout(displayMockData, 1000);
        } else {
            displayError(error.message);
        }
    }
}

input.new_string:
async function fetchWeather() {
    try {
        const response = await fetch(API_URL);

        if (!response.ok) {
            throw new Error(`API Error: ${response.status} - ${response.statusText}`);
        }

        const data = await response.json();
        displayWeather(data);

    } catch (error) {
        console.error('Error fetching weather:', error);
        displayError('Failed to fetch weather data. Please check your internet connection.');
    }
}

