   110	    </div>
   111	
   112	    <script>
   113	        // Using open-meteo.com - free weather API without API key
   114	        const GRAZ_LAT = 47.0707;
   115	        const GRAZ_LON = 15.4395;
   116	        const API_URL = `https://api.open-meteo.com/v1/forecast?latitude=${GRAZ_LAT}&longitude=${GRAZ_LON}&current=temperature_2m,relative_humidity_2m,apparent_temperature,is_day,precipitation,weather_code,surface_pressure,wind_speed_10m&timezone=Europe%2FVienna`;
   117	
   118	        function getWeatherIcon(weatherCode, isDay = true) {
   119	            // WMO weather codes to emoji mapping
   120	            const iconMap = {
   121	                0: isDay ? '☀️' : '🌙', // Clear sky
   122	                1: isDay ? '🌤️' : '🌙', // Mainly clear
   123	                2: isDay ? '⛅' : '☁️', // Partly cloudy
   124	                3: '☁️', // Overcast
   125	                45: '🌫️', // Fog
