   139	                75: '❄️', // Heavy snow fall
   140	                77: '🌨️', // Snow grains
   141	                80: '🌦️', // Slight rain showers
   142	                81: '🌧️', // Moderate rain showers
   143	                82: '🌧️', // Violent rain showers
   144	                85: '🌨️', // Slight snow showers
   145	                86: '❄️', // Heavy snow showers
   146	                95: '⛈️', // Thunderstorm
   147	                96: '⛈️', // Thunderstorm with slight hail
   148	                99: '⛈️'  // Thunderstorm with heavy hail
   149	            };
   150	            return iconMap[weatherCode] || '🌤️';
   151	        }
   152	
   153	        function formatTime(timestamp) {
   154	            return new Date(timestamp * 1000).toLocaleString('en-US', {
   155	                timeZone: 'Europe/Vienna',
