   157	                hour: '2-digit',
   158	                minute: '2-digit',
   159	                day: '2-digit',
   160	                month: '2-digit',
   161	                year: 'numeric'
   162	            });
   163	        }
   164	
   165	        function getWeatherDescription(weatherCode) {
   166	            const descriptions = {
   167	                0: 'Clear sky',
   168	                1: 'Mainly clear',
   169	                2: 'Partly cloudy',
   170	                3: 'Overcast',
   171	                45: 'Fog',
   172	                48: 'Depositing rime fog',
   173	                51: 'Light drizzle',
   174	                53: 'Moderate drizzle',
   175	                55: 'Dense drizzle',
   176	                56: 'Light freezing drizzle',
   177	                57: 'Dense freezing drizzle',
   178	                61: 'Slight rain',
   179	                63: 'Moderate rain',
   180	                65: 'Heavy rain',
   181	                66: 'Light freezing rain',
   182	                67: 'Heavy freezing rain',
   183	                71: 'Slight snow fall',
   184	                73: 'Moderate snow fall',
   185	                75: 'Heavy snow fall',
   186	                77: 'Snow grains',
   187	                80: 'Slight rain showers',
   188	                81: 'Moderate rain showers',
   189	                82: 'Violent rain showers',
   190	                85: 'Slight snow showers',
   191	                86: 'Heavy snow showers',
   192	                95: 'Thunderstorm',
   193	                96: 'Thunderstorm with slight hail',
   194	                99: 'Thunderstorm with heavy hail'
   195	            };
   196	            return descriptions[weatherCode] || 'Unknown';
   197	        }
   198	
   199	        function displayWeather(data) {
   200	            const weatherContent = document.getElementById('weather-content');
   201	            const current = data.current;
   202	            const icon = getWeatherIcon(current.weather_code, current.is_day);
   203	            const temp = Math.round(current.temperature_2m);
   204	            const feelsLike = Math.round(current.apparent_temperature);
   205	            const humidity = current.relative_humidity_2m;
   206	            const windSpeed = Math.round(current.wind_speed_10m);
   207	            const pressure = Math.round(current.surface_pressure);
   208	            const description = getWeatherDescription(current.weather_code);
   209	
   210	            weatherContent.innerHTML = `
   211	                <div class="city-name">Graz</div>
   212	                <div class="weather-icon">${icon}</div>
   213	                <div class="temperature">${temp}°C</div>
   214	                <div class="description">${description}</div>
