   218	                        <div class="detail-label">Feels like</div>
   219	                        <div class="detail-value">${feelsLike}°C</div>
   220	                    </div>
   221	                    <div class="detail-item">
   222	                        <div class="detail-label">Humidity</div>
   223	                        <div class="detail-value">${humidity}%</div>
   224	                    </div>
   225	                    <div class="detail-item">
   226	                        <div class="detail-label">Wind Speed</div>
   227	                        <div class="detail-value">${windSpeed} km/h</div>
   228	                    </div>
   229	                    <div class="detail-item">
   230	                        <div class="detail-label">Pressure</div>
   231	                        <div class="detail-value">${pressure} hPa</div>
   232	                    </div>
   233	                </div>
   234	                
   235	                <div class="last-updated">
   236	                    Last updated: ${new Date(current.time).toLocaleString('en-US', {
   237	                        timeZone: 'Europe/Vienna',
   238	                        hour12: false,
   239	                        hour: '2-digit',
   240	                        minute: '2-digit',
   241	                        day: '2-digit',
   242	                        month: '2-digit',
   243	                        year: 'numeric'
   244	                    })}
   245	                </div>
   246	            `;
   247	        }
   248	
   249	        function displayError(message) {
   250	            const weatherContent = document.getElementById('weather-content');
   251	            weatherContent.innerHTML = `
   252	                <div class="error">
   253	                    <strong>Error:</strong> ${message}
   254	                    <br><br>
   255	                    <small>Please check your internet connection or API key configuration.</small>
   256	                </div>
   257	            `;
   258	        }
   259	
   260	        async function fetchWeather() {
   261	            try {
   262	                const response = await fetch(API_URL);
   263	                
   264	                if (!response.ok) {
   265	                    throw new Error(`API Error: ${response.status} - ${response.statusText}`);
   266	                }
   267	                
   268	                const data = await response.json();
   269	                displayWeather(data);
   270	                
   271	            } catch (error) {
   272	                console.error('Error fetching weather:', error);
   273	                displayError('Failed to fetch weather data. Please check your internet connection.');
   274	            }
   275	        }
   276	
   277	        // Initial load
   278	        fetchWeather();
   279	
   280	        // Refresh every 10 minutes
   281	        setInterval(fetchWeather, 600000);
   282	    </script>
   283	</body>
