   194	                99: 'Thunderstorm with heavy hail'
   195	            };
   196	            return descriptions[weatherCode] || 'Unknown';
   197	        }
   198	
   199	        function displayWeather(data) {
   200	            const weatherContent = document.getElementById('weather-content');
   201	            const current = data.current;
