   242	                        month: '2-digit',
   243	                        year: 'numeric'
   244	                    })}
   245	                </div>
   246	            `;
   247	        }
   248	
   249	        function displayError(message) {
   250	            const weatherContent = document.getElementById('weather-content');
   251	            weatherContent.innerHTML = `
   252	                <div class="error">
   253	                    <strong>Error:</strong> ${message}
   254	                    <br><br>
   255	                    <small>Please check your internet connection or API key configuration.</small>
   256	                </div>
   257	            `;
   258	        }
   259	
   260	        async function fetchWeather() {
   261	            try {
   262	                const response = await fetch(API_URL);
   263	                
   264	                if (!response.ok) {
   265	                    throw new Error(`API Error: ${response.status} - ${response.statusText}`);
   266	                }
   267	                
   268	                const data = await response.json();
